import sqlite3def connect():    conn=sqlite3.connect("backend.db")    c=conn.cursor()    c.execute("""CREATE TABLE IF NOT EXISTS RMS (laundry_number text, quantity text,building text,room_number text,date text,delivery_date text)""")    conn.commit()    conn.close()    def addrecord(ref,futherinformation,building,room,date,delivery):    conn=sqlite3.connect("backend.db")    c=conn.cursor()    c.execute("INSERT INTO RMS VALUES(?,?,?,?,?,?)",(ref,futherinformation,building,room,date,delivery))    conn.commit()    conn.close() connect()